import { StatusBar } from "expo-status-bar";
import {
  Platform,
  StyleSheet,
  Image,
  Button,
  ScrollView,
  RefreshControl,
  TextInput,
  TouchableHighlight,
  UselessTextInput,
} from "react-native";
import { useEffect, useState } from "react";

import { Text, View } from "../components/Themed";
import firestore from "@react-native-firebase/firestore";
import storage from "@react-native-firebase/storage";
import * as ImagePicker from "expo-image-picker";
import { useRoute } from "@react-navigation/native";

export default function CreateScreen() {
  const route = useRoute();
  const [imageUri, SetImageUri] = useState(false);
  const [ProductName, SetProductName] = useState("");
  const [ProductPrice, SetProductPrice] = useState(null);
  const [ProductAbout, SetProductAbout] = useState(null);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== "web") {
        const { status } =
          await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== "granted") {
          alert("Sorry, we need camera roll permissions to make this work!");
        }
      }
    })();
  }, []);

  const saveData = async () => {
    const fileName = result.uri.split("/").pop();
    const reference = storage().ref(`/images/${fileName}`);
    await reference.putFile(result.uri).then(() => {
      firestore()
        .collection("Products")
        .add({
          name: ProductName,
          key: route.params.data,
          keytype: route.params.type,
          price: ProductPrice,
        })
        .then(() => {
          console.log("User added!");
        });
    });
  };

  const openCamera = async () => {
    // Ask the user for the permission to access the camera
    const permissionResult = await ImagePicker.requestCameraPermissionsAsync();

    if (permissionResult.granted === false) {
      alert("You've refused to allow this appp to access your camera!");
      return;
    }

    const result = await ImagePicker.launchCameraAsync({
      allowsEditing: true,
      aspect: [16, 9],
      quality: 1,
    });

    if (!result.cancelled) {
      console.log(result.uri);
      SetImageUri(result.uri);
    }
  };

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [16, 9],
      quality: 1,
    });

    if (!result.cancelled) {
      console.log(result.uri);
      SetImageUri(result.uri);
    }
  };

  const UselessTextInput = (props) => {
    return (
      <TextInput
        {...props} // Inherit any props passed to it; e.g., multiline, numberOfLines below
        editable
        maxLength={40}
      />
    );
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.image_box}>
        {imageUri && (
          <Image
            source={{
              uri: imageUri,
            }}
            style={{
              width: 350,
              height: 175,
              marginBottom: 5,
            }}
          />
        )}
        {!imageUri && (
          <Image
            source={require("../assets/images/UploadImage.png")}
            style={{
              width: 128,
              height: 128,
              marginBottom: 5,
            }}
          />
        )}

        <View style={styles.image_control_box}>
          <TouchableHighlight onPress={pickImage} style={styles.image_btn}>
            <Text style={styles.image_btn_text}>select image</Text>
          </TouchableHighlight>
          <TouchableHighlight onPress={openCamera} style={styles.image_btn}>
            <Text style={styles.image_btn_text}>open camera</Text>
          </TouchableHighlight>
        </View>
      </View>

      <TextInput
        style={styles.input}
        onChangeText={SetProductName}
        value={ProductName}
        placeholder="Product Name"
      />
      <TextInput
        style={styles.input}
        onChangeText={(number) => SetProductPrice(number)}
        value={ProductPrice}
        placeholder="Product Price Ex:10$"
        keyboardType="numeric"
      />

      <UselessTextInput
        multiline
        numberOfLines={4}
        onChangeText={(text) => SetProductAbout(text)}
        value={ProductAbout}
        style={styles.input}
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 16, paddingTop: 30, alignItems: "center" },
  input: {
    margin: 12,
    width: "100%",
    borderWidth: 1,
    padding: 10,
  },
  image_box: {
    backgroundColor: "gray",
    alignItems: "center",
    padding: 10,
    borderColor: "gray",
    borderRadius: 15,
  },
  image_control_box: {
    flexDirection: "row",
    backgroundColor: "transparent",
    padding: 5,
  },
  image_btn: {
    backgroundColor: "#4CAF50",
    marginHorizontal: 20,
    padding: 7,
    borderRadius: 10,
  },
  image_btn_text: {
    color: "white",
    textAlign: "center",
    fontSize: 20,
    fontWeight: "bold",
  },

  wrapper: {
    flexDirection: "row",
    backgroundColor: "#f6f8fa",
    color: "green",
  },
  title: { backgroundColor: "#f6f8fa", fontWeight: "bold" },
  row: { height: 28 },
  text: { textAlign: "center" },
  aboutTitle: {
    color: "red",
    fontSize: 20,
    borderBottomColor: "gray",
    borderBottomWidth: 3,
    marginTop: 20,
    fontWeight: "bold",
  },
  aboutBody: {
    marginTop: 5,
  },
});
